import { useState } from "react";
import { motion } from "framer-motion";
import { checkGift, markGiftUsed } from "../api";
import "./CheckGiftPage.css";

// ===== –°–ö–ê–ß–ò–í–ê–ù–ò–ï =====
async function downloadFile(url) {
  const res = await fetch(url);
  const blob = await res.blob();

  const blobUrl = window.URL.createObjectURL(blob);
  const a = document.createElement("a");
  a.href = blobUrl;
  a.download = "gift";
  document.body.appendChild(a);
  a.click();
  document.body.removeChild(a);
  window.URL.revokeObjectURL(blobUrl);
}

export default function CheckGiftPage() {
  const [code, setCode] = useState("");
  const [gift, setGift] = useState(null);
  const [message, setMessage] = useState("");
  const [checking, setChecking] = useState(false);
  const [opening, setOpening] = useState(false);

  // ===== –ü–†–û–í–ï–†–ö–ê =====
  const handleCheck = async () => {
    if (!code.trim() || checking) return;

    setChecking(true);
    setMessage("");
    setGift(null);

    try {
      const giftData = await checkGift(code.trim().toUpperCase());
      setGift(giftData);
      setMessage("üéâ –ö–æ–¥ –≤–µ—Ä–Ω—ã–π! –ù–∞–∂–º–∏—Ç–µ –Ω–∞ –ø–æ–¥–∞—Ä–æ–∫ üéÅ");
    } catch {
      setMessage("‚ùå –ù–µ–≤–µ—Ä–Ω—ã–π –∏–ª–∏ —É–∂–µ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–Ω—ã–π –∫–æ–¥");
    } finally {
      setChecking(false);
    }
  };

  // ===== –û–¢–ö–†–´–¢–ò–ï =====
  const handleOpenGift = async () => {
    if (!gift || opening) return;

    setOpening(true);

    try {
      await downloadFile(gift.file_url);
      await markGiftUsed(gift.code);
    } catch (e) {
      console.error("OPEN ERROR:", e);
    }
  };

  return (
    <div className="check-page">
      <motion.div
        className={`gift ${gift ? "active" : ""}`}
        onClick={handleOpenGift}
        animate={
          gift
            ? opening
              ? {
                  scale: [1, 1.3, 0.6],
                  rotate: [0, 10, -10, 0],
                  opacity: [1, 1, 0],
                }
              : { scale: [1, 1.08, 1] }
            : {}
        }
        transition={
          opening
            ? { duration: 1.2, ease: "easeInOut" }
            : gift
            ? { duration: 1.4, repeat: Infinity }
            : {}
        }
      >
        üéÅ
      </motion.div>

      {!gift && (
        <div className="code-box">
          <input
            placeholder="–í–≤–µ–¥–∏—Ç–µ —Å–µ–∫—Ä–µ—Ç–Ω—ã–π –∫–æ–¥"
            value={code}
            onChange={(e) => setCode(e.target.value.toUpperCase())}
          />
          <button onClick={handleCheck} disabled={checking}>
            {checking ? "–ü—Ä–æ–≤–µ—Ä–∫–∞..." : "–ü—Ä–æ–≤–µ—Ä–∏—Ç—å –∫–æ–¥"}
          </button>
        </div>
      )}

      {message && <div className="hint">{message}</div>}
    </div>
  );
}
